<template>
  <div class="login">
    <div class="form-box">
      <h2>会议报名管理系统</h2>
      <a-form
        id="components-form-demo-normal-login"
        :form="form"
        class="login-form"
        @submit="handleSubmit"
      >
        <a-form-item>
          <a-input
            v-decorator="[
              'userName',
              {
                rules: [
                  { required: true, message: '请输入用户名' },
                ],
              },
            ]"
            placeholder="用户名"
          >
            <a-icon
              slot="prefix"
              type="user"
              style="color: rgba(0, 0, 0, 0.25)"
            />
          </a-input>
        </a-form-item>
        <a-form-item>
          <a-input
            v-decorator="[
              'password',
              {
                rules: [
                  { required: true, message: '请输入密码' },
                ],
              },
            ]"
            type="password"
            placeholder="密码"
          >
            <a-icon
              slot="prefix"
              type="lock"
              style="color: rgba(0, 0, 0, 0.25)"
            />
          </a-input>
        </a-form-item>
        <a-form-item>
          <a-button type="primary" html-type="submit" class="login-form-button">
            登录
          </a-button>
        </a-form-item>
      </a-form>
    </div>
  </div>
</template>

<script>
import Vue from "vue";
import { Form, Input, Button } from "ant-design-vue";
Vue.use(Form).use(Input).use(Button);
export default {
  name: "Login",
  data() {
    return {};
  },
  beforeCreate() {
    this.form = this.$form.createForm(this, { name: "normal_login" });
  },
  created() {},
  methods: {
    handleSubmit(e) {
      e.preventDefault();
      this.form.validateFields((err, values) => {
        if (!err) {
          this.axios.post("/login",{
            "username":values.userName,
            "password":values.password
          }).then(res=>{
            let data = res.data.data
            this.$store.commit("tokenM",data.token)
            this.$store.commit("userM", data.user)
            this.$router.push("/")
          }).catch(err=>{
            console.log(JSON.stringify(err))
          })
        }
      });
    },
  },
};
</script>

